var TE_HTML = function(target, o) {

    var editor = new TE(target),
        ui = editor.ui(o);

    ui.extend(ui.tools, {
        bold: {
            title: 'Bold',
            click: function(e, $) {
                $.command('strong');
            }
        },
        italic: {
            title: 'Bold',
            click: function(e, $) {
                $.command('em');
            }
        },
        underline: {
            title: 'Bold',
            click: function(e, $) {
                $.command('u');
            }
        },
        strike: {
            i: 'strikethrough',
            title: 'Delete',
            click: function(e, $) {
                $.command('del');
            }
        },
        link: {
            title: 'Link',
            click: function(e, $) {
                $.ui.modal({
                    header: 'URL',
                    body: '<input type="text" class="block">',
                    footer: '<button>OK</button><button>Cancel</button>'
                });
            }
        }
    });

    ui.extend(ui.keys, {
        'control+b': 'bold'
    });

    ui.config.tools = 'eraser | bold italic underline strike | link image | superscript subscript | paragraph | header | quote-left code | list-ul list-ol | align-left align-center align-right align-justify | table | undo redo';

    return ui.create();

};